import { useState } from 'react';
import axios from 'axios';
import Lottie from 'lottie-react';
import title from '../assets/title.json'; 
// import icons from '../assets/woman2.json'; 
// import icons from '../assets/man2.json'; 
import icons from '../assets/man3.json'; 
import { useNavigate } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
axios.defaults.withCredentials = true;

const SignIn = () => {
    const navigate = useNavigate();
    const [username, set_username] = useState("");
    const [password, set_password] = useState("");

    const[pass_eye,set_pass_eye]=useState(true)

    function sleep(ms: number): Promise<void> {
        return new Promise(resolve => setTimeout(resolve, ms));
    }


    const initiate_signin=async()=>{
        const data={
            username:username,
            password:password
        }
        const response = await axios.post("http://localhost:5500/api/signin",data)
        if(response.status===200){
            sleep(1000)
            navigate("/dashboard")
        }
        else{
            toast.error("Invalid Username or Password!")
        }
    }

    return (
        <div id="bg">
            <ToastContainer/>
            <nav className="flex justify-between items-center m-8">
                <div className="h-8">
                    <Lottie
                        animationData={title}
                        loop={false} 
                        autoplay={true}
                        style={{ width: '100%', height: '100%' }} 
                    />
                </div>
                <div className="flex items-center">
                    <button 
                        className="bg- text-white pt-2 pb-2 pl-4 pr-4 rounded-full bg-opacity-20 border-white border-2"
                        onClick={() => { navigate("/signin") }}
                    >
                        Log In
                    </button>
                </div>
            </nav>

            <div className="flex justify-center mt-24 gap-16">
                <div className="drop-shadow-2xl h-[60vh]">
                    <Lottie className="drop-shadow-2xl"
                        animationData={icons}
                        loop={true} 
                        autoplay={true}
                        style={{ width: '100%', height: '100%' }} 
                    />
                </div>

                <div className="w-2/4 flex flex-col items-center justify-center">
                    {/* Username Input */}
            <div className="w-8/12">
                <input
                    type="text"
                    onChange={(e) => set_username(e.target.value)}
                    value={username}
                    placeholder="Username"
                    className="w-full outline-none text-white text-center border-none rounded-2xl transition-all duration-300 bg-white bg-opacity-[3%] p-2 focus:drop-shadow-2xl focus:shadow-2xl drop-shadow shadow placeholder:text-gray-300 ease-in-out focus:bg-opacity-5"
                />
            </div>

            {/* Password Input */}
            <div className="mt-4 flex flex-row w-8/12 justify-center">
                <input
                    type={pass_eye?"password":"text"}
                    onChange={(e) => set_password(e.target.value)}
                    value={password}
                    placeholder="Password"
                    className="placeholder:pl-10 w-full outline-none text-white text-center border-none rounded-l-2xl transition-all duration-300 bg-white bg-opacity-[3%] p-2 focus:drop-shadow-2xl focus:shadow-2xl drop-shadow shadow placeholder:text-gray-300 focus:bg-opacity-5 ease-in-out"
                />
